import React,{ Component } from 'react'
import classes from './Auth.module.css'
import formlogo from '../../assets/clip.svg'

class Auth extends Component{

    state={
        details:{
            email:null,
            password:null,
        },
        isSignup:false
    }

    formchange = () => {
        this.setState(prevstate=>{
            return{
                isSignup:!prevstate.isSignup
            }
        })
    }

    render(){
        return(
            <header className={classes.back}>
                <h1>Heading</h1>
                <div className={classes.form}>
                    <img src={formlogo}></img>
                    <p style={{textAlign:"center",margin:"20px 0px",fontSize:"170%"}}>{this.state.isSignup?"SIGN UP":"SIGN IN"}</p>
                    <form>
                        <input type="email" id="email" name="email" placeholder="E-mail"></input>
                        <input type="password" id="password" name="password" placeholder="Password"></input>
                        <button className={classes.submit}>Submit</button>
                    </form>
                    <p style={{marginTop:"25px"}}>Don't have an account?</p>
                    <button className={classes.secondbtn} onClick={this.formchange}>{!this.state.isSignup?"SIGN UP":"SIGN IN"}</button>
                </div>
            </header>
        )
    }
}

export default Auth;
