import React from 'react'
import Hotel from './Hotel/Hotel'

const hotels = (args) => {
    let hotels=args.hotels;
    let hotel=null;
    if(hotels===null){
        console.log("insidehotels",hotels)
        args.route.push("/search")
    }
    else{
        hotel=hotels.map((el,index)=>{
            return <Hotel key={index} name={el.name} address={el.address} rating={el.rating}
                price={el.price} />
        })
    }

    return(
        <div>
            {hotel}
            {/* <Hotel name="Hotel-1" address="addr123" rating="9.1" price="Rs 3000"/>
