import React, { Component } from 'react'
import classes from './Account.module.css'
import Toolbar from '../../components/UI/Toolbar/Toolbar'

class Account extends Component{

    state={
        userdetails:{
            firstname:null,
            surname:null,
            contactnumber:null,
            address:null
        }
    }

    valuestore=(event,name)=>{
        let dummystate={...this.state.userdetails}
        dummystate[name]=event.target.value;
        this.setState({
            userdetails:dummystate
        })
    }

    btnclick=()=>{

    }

    render(){
        return(
            <div style={{backgroundColor:"#eee",height:"100vh"}}>
                <Toolbar/>
                <div className={classes.outaccount}>
                    <form className={classes.formaccount}>
                        <div className={classes.inaccount}>
                            <label className={classes.labelaccount} for="fname">First Name</label>
                            <input onChange={(event)=>this.valuestore(event,"firstname")} className={classes.inputaccount} type="text" id="fname" name="firstname"></input>
                        </div>
                        <div className={classes.inaccount}>
                            <label className={classes.labelaccount} for="sname">Surname</label>
                            <input onChange={(event)=>this.valuestore(event,"surname")} className={classes.inputaccount} type="text" id="sname" name="surname"></input>
                        </div>
                        <div className={classes.inaccount}>
                            <label className={classes.labelaccount} for="cnum">Contact Number</label>
                            <input onChange={(event)=>this.valuestore(event,"contactnumber")} className={classes.inputaccount} type="text" id="cnum" name="contactnumber"></input>
                        </div>
                        <div className={classes.inaccount}>
                            <label className={classes.labelaccount} for="add">Address</label>
                            <input onChange={(event)=>this.valuestore(event,"address")} className={classes.inputaccount} type="text" id="add" name="address"></input>
                        </div>            
                        <button className={classes.btnbook}>Book</button>
                    </form>
                </div>
            </div>
        )
    }
}

export default Account;
