import React, { Component } from 'react'
import Toolbar from '../../components/UI/Toolbar/Toolbar'
import classes from './Search.module.css'
import Hotels from '../../components/Hotels/Hotels'
import * as searchactions from '../../store/actions/Searchactions'
import { connect } from 'react-redux'

class Search extends Component{

    state={
        details:{
            city:"Pune",
            rating:null,
            date1:null,
            date2:null,
            rooms:null
        },
        submit:false
    }

    storevalues = (event,name) => {
        let dummydetails={...this.state.details};
        dummydetails[name]=event.target.value;
        this.setState({
            details:dummydetails
        })
    }

    onsubmit=(event)=>{
        event.preventDefault();
        console.log("searchsubmit");
        this.setState({
            submit:true
        })
        this.props.submit(this.state.details);
    }

    render(){
        // console.log(new Date('2011-04-11Z'));
        return(
            <div className={classes.body}>
                <Toolbar/>
                <div className={classes.form}>
                    <p style={{fontSize:"120%",marginBottom:"20px"}}>Search your Ideal Hotel</p>
                    <form>
                        <div className={classes.inputcont}>
                            <select onChange={(event)=>this.storevalues(event,"city")} className={classes.input} name="place" placeholder="City">
                                <option value="Pune">Pune</option>
                                <option value="Mumbai">Mumbai</option>
                                <option value="Delhi">Delhi</option>
                            </select>
                            <input className={classes.input} type="text" name="start" placeholder="Rated(1-5)" onChange={(event)=>this.storevalues(event,"rating")}></input>
                        </div>
                        <div className={classes.inputcont}>
                            <input className={classes.input} type="text" name="firstdate" placeholder="Check-in(YYYY-MM-DD)" onChange={(event)=>this.storevalues(event,"date1")}></input>
                            <input className={classes.input} type="text" name="seconddate" placeholder="Check-out(YYYY-MM-DD)" onChange={(event)=>this.storevalues(event,"date2")}></input>
                        </div>
                            <input className={classes.input} onChange={(event)=>this.storevalues(event,"rooms")} style={{width:"50%",display:"block",marginTop:"20px",marginLeft:"auto",marginRight:"auto"}} type="text" name="rooms" placeholder="No. of Rooms"></input>
                        <button className={classes.submit} onClick={this.onsubmit} >Search</button>
                    </form> 
                </div>
            </div>
        )
    }
}

const mapstatetoprops = state => {
    return{
        hotels:state.searchreducer.hotels
    }
}

const mapactionstoprops = dispatch => {
    return{
        submit:(details)=>{dispatch(searchactions.search(details))}
    }
}

export default connect(mapstatetoprops,mapactionstoprops)(Search);
