import axios from '../../axios'

export const searchsuccess = (hotels) => {
    return{
        type:"SEARCH_SUCCESS",
        hotels:hotels
    }
}

export const search = (details) => {
    return dispatch => {
        // let url="/"+details.city+"/"+details.rating+".json"
        // console.log(url);
        axios.get("Pune/5")
        .then(res=>{
            console.log(res.data)
            dispatch(searchsuccess(res.data))
        })
        .catch(err=>{
            console.log("Error in search reducer")
        })
    }
}
