let intialstate={
    hotels:null,
    date1:null,
    date2:null,
    numrooms:null,
}

const reducer = (state=intialstate,action) => {
    switch(action.type){
        case "SEARCH_SUCCESS":
            return{
                ...state,
                hotels:action.hotels
            }
    }
    return state;
}

export default reducer;
