let intialstate={
    user:null
}

const reducer = (state=intialstate,action) => {
    switch(action.type){
        case "ACCOUNTDETAILS_SUCCESS":
            return{
                ...state,
                user:action.user
            }
    }
    return state;
}

export default reducer;
