import axios from '../../axios'

export const fetchaccountdetails_success=user=>{
    return{
        type:"ACCOUNTDETAILS_SUCCESS",
        user:user
    }
}

export const fetchaccountdetails = (token,userid) => {
    return dispatch => {
        axios.get("https://hotelapp-f223c.firebaseio.com/users.json?auth="+token+'&orderBy="userId"&equalTo="'+userid+'"')
        .then(res=>{
            dispatch(fetchaccountdetails_success(res.data))
        })
        .catch(err=>{
            console.log("Error in Accountactions")
        })
    }
}
