import React from 'react'
import Toolbar from '../UI/Toolbar/Toolbar'

const layout = (args) => {
    // console.log(args.check)
    let toolbar=<Toolbar/> ;
    if(args.name==="Auth")
        toolbar=null
    return(
        <div>
            {toolbar}
            <main>{args.children}</main>
        </div>
    )
}

export default layout
