import React from 'react'
import Hotel from './Hotel/Hotel'

const hotels = (args) => {
    console.log("hotels",args.hotels)
    let hotel=args.hotels.map((el,index)=>{
        <Hotel key={index} name={el.name} address={el.address} rating={el.rating}
            price={el.price} />
    })
    return(
        <div>
            {hotel}
