import React from 'react'
import { Redirect } from 'react-router';
import Hotel from './Hotel/Hotel'

const hotels = (args) => {
    let hotels=args.hotels;
    console.log("hotels",hotels)
    let hotel=hotels.map((el,index)=>{
        return <Hotel key={index} name={el.name} address={el.address} rating={el.rating}
            price={el.price} />
    })
    return(
        <div>
            {this.props.hotels?null:<Redirect to="/search"/>}
            {hotel}
            {/* <Hotel name="Hotel-1" address="addr123" rating="9.1" price="Rs 3000"/>
