import axios from '../../axios'

export const fetchaccountdetails_success=user=>{
    return{
        type:"ACCOUNTDETAILS_SUCCESS",
        user:user
    }
}

export const fetchaccountdetails = (token,userid) => {
    return dispatch => {
        axios.get("https://hotelapp-f223c.firebaseio.com/users.json?auth="+token+'&orderBy="userId"&equalTo="'+userid+'"')
        .then(res=>{
            let key=Object.keys(res.data)
            let user={...res.data[key]}
            dispatch(fetchaccountdetails_success(user))
        })
        .catch(err=>{
            console.log("Error in Accountactions")
        })
    }
}

export const saveaccountdetails_success = user => {
    return{
        type:"SAVEACCOUTNDETAILS_SUCCESS",
        user,user
    }
}

export const saveaccountdetails = (user,token,userid) => {
    return dispatch => {
        // console.log(userid)
        console.log(user)
        axios.get("https://hotelapp-f223c.firebaseio.com/users.json?auth="+token+'&orderBy="userId"&equalTo="'+userid+'"')
        .then(res=>{
            // console.log(res.data)
            let key=Object.keys(res.data)
            // console.log("Key",key[0])
            axios.put("https://hotelapp-f223c.firebaseio.com/users/"+key[0]+".json?auth="+token,user)
            .then(res=>{
                dispatch(saveaccountdetails_success(user))
            })
            .catch(err=>{
                console.log("Error in Save Account")
            })
        })
        .catch(err=>{
            console("ERROR")
        })

    }
}
