import React from 'react'
import Hotel from './Hotel/Hotel'

const hotels = (args) => {
    let hotels=args.hotels;
    console.log("insidehotels",hotels)
    let hotel=null;
    if(hotels===null){
        args.route.push("/search")
    }
    else{
        let arraykeys=Object.keys(hotels);
        hotel=arraykeys.map((el,index)=>{
                return <Hotel key={index} name={hotels[el].name} address={hotels[el].address} rating={hotels[el].rating}
                price={hotels[el].price} />
        })  
    }

    return(
        <div>
            {hotel}
            {/* <Hotel name="Hotel-1" address="addr123" rating="9.1" price="Rs 3000"/>
            <Hotel name="Hotel-2" address="addr456" rating="9.4" price="Rs 5000"/>
            <Hotel name="Hotel-3" address="addr789" rating="9.2" price="Rs 4500"/> */}
        </div>
    )
}

export default hotels;
