import React, { Component } from 'react'

class Upload extends Component{

    state={
        image:null
    }

    storeimage=(event)=>{
        this.setState({
            image:event.target.files[0]
        })
    }

    render(){
        return(
            <div>
                <form>
                    <input type="file" onChange={this.storeimage}></input>
                    <button>Save</button>
                </form>
             </div>
        )
    }
}

export default Upload;
