let intialstate={
    token:null,
    userid:null,
    loading:false,
    error:null,
}
const Authreducer = (state=intialstate,action) => {
    switch(action.type){
        case 'AUTH_SUCCESS':
            return{
                ...state,
                token:action.token,
                userid:action.userid
            }
        case "AUTH_FAIL":
            return{
                ...state,
                error:action.error
            }    
    }
    return state;
}

export default Authreducer
