import axios from 'axios'

export const authstart = () => {
    return{
        type:"AUTH_START"
    }
}

export const authsuccess = (token,userid) => {
    return{
        type:"AUTH_SUCCESS",
        token:token,
        userid:userid
    }
}

export const authfail = (error) => {
    return{
        type:"AUTH_FAIL",
        error:error
    }
}
export const logout=()=>{
    return{
        type:"LOGOUT"
    }
}
export const booksuccess=(details)=>{
    return{
        type:"BOOK_HOTEL",
        details:details
    }
}
export const bookhotel=(token,details)=>{
    // console.log("Route",route)
    return dispatch=>{
        axios.post("https://hotelapp-f223c.firebaseio.com/bookings.json?auth+"+token,details)
        .then(res=>{
            dispatch(booksuccess(details));
        })
        .catch(err=>{
            console.log(err);
        })
        route.push("/search");
    }
}

export const auth = (email,password,siginup,route) => {
    let authdata={
        email:email,
        password:password,
        returnSecureToken:true
    }
    let url="https://identitytoolkit.googleapis.com/v1/accounts:signUp?key=AIzaSyAxcsB7Wsoy3OFDVFWrsxd9MsiTKw1s6Wk";
    if(!siginup){
        url="https://identitytoolkit.googleapis.com/v1/accounts:signInWithPassword?key=AIzaSyAxcsB7Wsoy3OFDVFWrsxd9MsiTKw1s6Wk"
    }
    return dispatch => {
        dispatch(authstart())
        axios.post(url,authdata)
        .then(res=>{
            console.log(res.data)
            dispatch(authsuccess(res.data.idToken,res.data.localId))
            route.push("/search")
        })
        .catch(err=>{
            console.log(err);
            dispatch(authfail(err.response.data.error))
        })
    }
}
