import React, { Component } from 'react'
import 'font-awesome/css/font-awesome.min.css';
import classes from './Hotels.module.css'
import Modal from '../UI/Modal/Modal'

class hotels extends Component{
    render(){
        let hotels=this.props.hotels;
    let hotel=null;
    if(hotels===null){
        this.props.route.push("/search")
    }
    else{
        let arraykeys=Object.keys(hotels);
        // hotel=arraykeys.map((el,index)=>{
        //         return <Hotel key={index} image={hotels[el].imageurl} name={hotels[el].name} address={hotels[el].address} rating={hotels[el].rating}
        //         price={hotels[el].price*args.rooms} />
        // }) 
        hotel=arraykeys.map((el,index)=>{
            return (
                <div style={{width:"100%",backgroundColor:"#eee",padding:"20px 0px"}}>
                <div className={classes.hotel}>
                        <img src={hotels[el].imageurl}></img>
                    <div className={classes.maindiv}>
                        <div className={classes.betweendiv} style={{paddingBottom:"20px",paddingTop:"10px"}}>
                            <div className={classes.interdiv}>
                                <i class="fa fa-home" aria-hidden="true" style={{fontSize:"111.3%"}}> {hotels[el].name}</i>
                            </div>
                            <div className={classes.interdiv}>
                                <i class="fa fa-star-o" aria-hidden="true" style={{fontSize:"111.3%"}}>  {hotels[el].rating}</i>
                            </div>
                            <div className={classes.interdiv}>
                            <   i class="fa fa-money" aria-hidden="true" style={{fontSize:"111.3%"}}>  {hotels[el].price*this.props.rooms}</i>
                            </div>
                        </div>
                        <div className={classes.betweendiv}>
                            <div className={classes.interdiv} style={{borderRight:"1px solid transparent",borderBottom:"1px solid #ccc",textAlign:"left"}}>
                                <i class="fa fa-map-marker" aria-hidden="true" style={{fontSize:"111.3%"}}> {hotels[el].address}</i>
                            </div>              
                        </div>
                        <div className={classes.betweendiv} style={{paddingTop:"5px"}}>
                            <div className={classes.interdiv} style={{borderRight:"1px solid transparent"}}>
                                <button className={classes.btnview}>View</button>
                            </div>
                            <div className={classes.interdiv} style={{borderRight:"1px solid transparent"}}>
                                <button className={classes.btnbook}>Book</button>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
            );
    })  
    }
        return(
            <div>
                {hotel}
            </div>
        )
    }
}
export default hotels;
